head.script:
/**
 * 화면 pageload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */ 
scwin.onpageload = function() {
    var dlt_param   = com.data.getParameter();
    dlt_param = ssgo.setReversLst(dlt_param);

    dlt_rcntSbmsnDocmtLst.removeAll();
    dlt_rcntSbmsnDocmtLst.setJSON(dlt_param);
    
    if(!scutilo.isEmpty(dlt_param) && !scutilo.isEmpty(dlt_param[0].execIntgCsNmCd)){
        // 채권압류 및 전부명령 또는 추심명령일 경우 메시지 표시
        var execIntgCsNmCd = dlt_param[0].execIntgCsNmCd;
        if(execIntgCsNmCd == "10" || execIntgCsNmCd == "11" || execIntgCsNmCd == "12" || execIntgCsNmCd == "13"
        || execIntgCsNmCd == "15" || execIntgCsNmCd == "16" || execIntgCsNmCd == "20" || execIntgCsNmCd == "23") {
            txt_bndAtchmMsg.show("");
        } else {
            txt_bndAtchmMsg.hide();
        }
    } else {
        txt_bndAtchmMsg.hide();
    }

    // 조회건수 없을시 버튼숨김
    if(dlt_rcntSbmsnDocmtLst.getRowCount() == 0) {
        btn_csDocProgList.hide();
    }

    // 그리드 사이즈조정시 캔버스뒤로 숨는 오류처리
    ssgo.setGrdResizeAddEvt(grd_rcntSbmsnDocmtLst);  
};

/**
 * 화면 pageunload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.onpageunload = function() {
};
	
/**
 * 내용필드 expression
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.04.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.setExpressOfdocCtt = function (colIdx) {
    var gridObj   = dlt_rcntSbmsnDocmtLst;
    var content1 = gridObj.getCellData(colIdx, "content1");
    var content2   = gridObj.getCellData(colIdx, "content2");
    var content3   = gridObj.getCellData(colIdx, "content3");    

    return content1 + " " +  content2 + " " + content3;
}
