head.script:
/**
 * 화면 pageload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */ 
scwin.onpageload = function() {
    var dlt_param   = com.data.getParameter() || ssgo.getParameter();
    var comTaskTypCd = "";
    
    dlt_btprtCttLst.removeAll();
    dlt_btprtCttLst.setJSON(dlt_param);
    
    // 당사자 수가 설정값을 초과할 경우 안내문구 표시
    txt_lstMsg.hide();

    if(ssgo.getProperties("clt.ssgo.btprt.page.size") <= dlt_btprtCttLst.getTotalRow() ) {
        let csDvsCd = $p.parent().dma_search.get("csDvsCd");
        comTaskTypCd = ssgo.getProperties("ctt.csDvsCd." + csDvsCd);

        if(comTaskTypCd == scglblo.TASK.CV
         ||comTaskTypCd == scglblo.TASK.HJ
         ||comTaskTypCd == scglblo.TASK.ET 
         ||comTaskTypCd == scglblo.TASK.GJ 
         ||comTaskTypCd == scglblo.TASK.HP){ // 민사, 항고재항고, 기타사건, 기타집행, 회생파산
            txt_lstMsg.show();
        } else {
            txt_lstMsg.hide();
        }
    }

    // 그리드 사이즈조정시 캔버스뒤로 숨는 오류처리
    ssgo.setGrdResizeAddEvt(grd_btprtCttLst);  

};

/**
 * 화면 pageunload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.onpageunload = function() {
};
