head.script:
/**
 * 화면 pageload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */  
scwin.onpageload = function() {
    var dlt_param   = com.data.getParameter() || ssgo.getParameter();
    
    dlt_scrtyCttLst.removeAll();
    dlt_scrtyCttLst.setJSON(dlt_param);

    // 그리드 사이즈조정시 캔버스뒤로 숨는 오류처리
    ssgo.setGrdResizeAddEvt(grdScrtyCttLst);        
};

/**
 * 화면 pageunload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.onpageunload = function() {
};

/**
 * 그리드 내용필드 expression
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.04.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.setExpScrtyCttLst = function (colIdx) {
    var gridObj  = dlt_scrtyCttLst;
    var scrtyAmt = gridObj.getCellData(colIdx, "scrtyAmt");
    var prsvDspsScrtyDvsNm = gridObj.getCellData(colIdx, "prsvDspsScrtyDvsNm");

    return prsvDspsScrtyDvsNm + "  " + scutilo.getCommaFmt(scrtyAmt) + "원";    
}
