 * ============ ============ ==========================
 */
scwin.onpageunload = function() {
};

/**
 *********************************************************************************************************
 * Submission Event Handler
 *********************************************************************************************************
 */
/**
 * submit 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbm_search_submit = function(e) {
}; 
 
/**
 * submitdone 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbm_search_submitdone = function(e) {
    var csRsltData = e.responseJSON.data;
    
    // 새로 열릴 화면에 데이터를 전달 (dataObject를 통해 전달 가능)         
    var wfObj = {"dataObject": {"type" : "json", "name" : "paramData", "data" : null,}}; 
    
    // 가공처리되는 정보 ------------------------------------------------
    scwin.ufn_setDataArilPrcs();
    
    // 심급내용
    if(!ssgo.isLenZero(csRsltData.dlt_inscrtDtsLst)) {
        wfObj.dataObject.data = csRsltData.dlt_inscrtDtsLst;
        wfInscrtDtsLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F10.xml"), wfObj);    
    }

    if(dma_csBasCtt.get("isHrngProgCurst") === "Y" ) {
        // 심리진행현황
        wfObj.dataObject.data = csRsltData.dlt_hrngProgCurst;
        wfHrngProgCurst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F20.xml"), wfObj);    
    } else {
        // 최근기일내용
        wfObj.dataObject.data = csRsltData.dlt_rcntDxdyLst;
        
	    // 소요예상시간 공개 사건 조회 시 소요예상시간이 표기처리
	    if(dma_csBasCtt.get("dxdyRqrdSchdTimeRlsYn") == "Y") {
	        wfRcntDxdyLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F30.xml"), wfObj);  // 6열
	    } else {
	        wfRcntDxdyLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F32.xml"), wfObj);  // 5열    
	    }        
    }

    // 최근 제출서류 접수내용
    wfObj.dataObject.data = csRsltData.dlt_rcntSbmsnDocmtLst;
    wfRcntSbmsnDocmtLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F40.xml"), wfObj);    

    // 관련사건내용
    if(!ssgo.isLenZero(csRsltData.dlt_reltCsLst)) {
        wfObj.dataObject.data = csRsltData.dlt_reltCsLst;
        wfReltCsLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F50.xml"), wfObj);    
    }

    // 당사자내용
    if(!ssgo.isLenZero(csRsltData.dlt_btprtCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_btprtCttLst;
        wfBtprtCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F60.xml"), wfObj);    
    }

    // 대리인내용
    if(!ssgo.isLenZero(csRsltData.dlt_agntCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_agntCttLst;
        wfAgntCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F70.xml"), wfObj);    
    }

    // 소송관계인내용
    if(!ssgo.isLenZero(csRsltData.dlt_lwstRltnrCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_lwstRltnrCttLst;
        wfLwstRltnrCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F80.xml"), wfObj);    
    }

    // 사건검색 후  callback
    ssgo.callbackCsSrchDetail(scwin.param, e);
    
};    

scwin.sbm_search_submiterror = function(e) {
    
    // 사건검색 후  callback    
    ssgo.callbackCsSrchDetail(scwin.param, e);
};


