/**
 * 화면제어 및 가공되는 데이터를 처리한다.
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * 2025.07.24.   김윤수       [25A-SSG-0018]본안사건의 상소인 정보 표시 개선
 * ============ ============ ==========================
 */
scwin.ufn_setDataArilPrcs = function() {
    
    // 영상 여부
    if(dma_csBasCtt.get("vdeoJdcpcProgYn") != "Y") {
        txt_isVdeo.hide();
    }
    // 전자 여부
    if(dma_csBasCtt.get("lwstDvsCd") !== "2") {
        txt_isElctn.hide();
    }
    
    // 원고, 피고 레이블구성
    txt_rprsClmntNm.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("rprsClmntNm"), dma_csBasCtt.get("clmntCnt")));
    txt_rprsAcsdNm.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("rprsAcsdNm"), dma_csBasCtt.get("acsdCnt")));

    var cfupMarkNm = "";
    if(scutilo.isEmpty(dma_csBasCtt.get("jdbnCd"))) {
        cfupMarkNm = "미배당";
    } else {
        cfupMarkNm = dma_csBasCtt.get("cfupMarkNm");
    }

    // 재판부 fulltext구성
    var txtjdbnNm = dma_csBasCtt.get("jdbnNm");
    if( !scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd")) && !scutilo.isEmpty(dma_csBasCtt.get("ultmtJdbnNm")) ) {
        txtjdbnNm = dma_csBasCtt.get("ultmtJdbnNm");
    }
    if("000100" !== dma_csBasCtt.get("cortCd") && !scutilo.isEmpty(cfupMarkNm)) {
        txtjdbnNm += "(" + cfupMarkNm + ")";
    }
    if(dma_csBasCtt.get("csPrsrvYn") !== "Y" && !scutilo.isEmpty(dma_csBasCtt.get("jdbnTelno"))) {
        txtjdbnNm += "(전화:" + dma_csBasCtt.get("jdbnTelno") + ")";
    }
    txt_jdbnNm.setValue(txtjdbnNm);

    // 종국내용은 현재날짜보다 작을 경우에만 보여줌
    let today = scdateo.getDateStr(new Date());
    if(!scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd")) && scdateo.getDaysBetween(dma_csBasCtt.get("csUltmtYmd"), today) >= 0) {
        var csUltmtYmdFmt = (!scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("csUltmtYmd")), 'yyyy.MM.dd') : "";
        txt_csUltmtDvsNm.setValue(csUltmtYmdFmt + " " + dma_csBasCtt.get("csUltmtDvsNm"));
    } else  {
        txt_csUltmtDvsNm.setValue("");
    }    
    
    // 상소인 정보 셋팅
    var aplntCtt = "";
    if(dlt_aplntCttLst.getRowCount() > 0) {
        if(dlt_aplntCttLst.getRowCount() == 1) {
            let prtcpnDvsCd = dlt_aplntCttLst.getCellData(0, "prtcpnDvsCd");
            if(prtcpnDvsCd == "04" || prtcpnDvsCd == "05") {
                aplntCtt = dlt_aplntCttLst.getCellData(0, "btprtDvsNm") + "보조참가";
            } else {
                aplntCtt = dlt_aplntCttLst.getCellData(0, "btprtDvsNm");
            }
        } else if(dlt_aplntCttLst.getRowCount() == 2) {
            for(let i = 0; i < dlt_aplntCttLst.getRowCount(); i++) {
                let prtcpnDvsCd = dlt_aplntCttLst.getCellData(i, "prtcpnDvsCd");
                if(prtcpnDvsCd == "04" || prtcpnDvsCd == "05") {
                    aplntCtt += dlt_aplntCttLst.getCellData(i, "btprtDvsNm") + "보조참가";
                } else {
                    aplntCtt += dlt_aplntCttLst.getCellData(i, "btprtDvsNm");
                }
                if(i == 0) {
                    aplntCtt += ", ";
                }
            }
        } else if(dlt_aplntCttLst.getRowCount() == 3) {
            for(let i = 0; i < dlt_aplntCttLst.getRowCount(); i++) {
                var comma = ", ";
                if(i == 2) {
                    comma = "";
                }
                let prtcpnDvsCd = dlt_aplntCttLst.getCellData(i, "prtcpnDvsCd");
                if(prtcpnDvsCd == "04" || prtcpnDvsCd == "05") {
                    aplntCtt += dlt_aplntCttLst.getCellData(i, "btprtDvsNm") + "보조참가"+ comma;
                } else {
                    aplntCtt += dlt_aplntCttLst.getCellData(i, "btprtDvsNm") + comma;
                }
            }
        }
    }
    txt_aplntCtt.setValue(aplntCtt);

    //사물관할코드에 따른 분기처리
    if(dma_csBasCtt.get("intgInscrtMtjrCd") == "5") {
        // 상고기록접수통지서 visiable
        tr_dlvrCrtYmd.show("");
        tr_aplRjctnInf.hide();
        tr_aplRjctnYmdUp.hide();
        tr_aplRjctnYmdDw.hide();

    } else {
        tr_aplRjctnInf.show("");
        
        // 보존,폐기 view
        if(!scutilo.isEmpty(dma_csBasCtt.get("prsvCtt"))) {
            if(dma_csBasCtt.get("prsvCtt") === "보존") {
                txt_titPrsvCtt.setValue("보존여부");
                txt_prsvCtt.setValue("기록보존됨");
            } else if(dma_csBasCtt.get("prsvCtt") === "폐기") {
                txt_titPrsvCtt.setValue("폐기여부");
                txt_prsvCtt.setValue("기록폐기됨");
            }
            tr_aplRjctnYmdUp.show("");
            tr_aplRjctnYmdDw.hide();
        } else  {
            tr_aplRjctnYmdDw.show("");
            tr_aplRjctnYmdUp.hide();
        }
        tr_dlvrCrtYmd.hide();   // 상고기록접수통지서 visiable
    }

    // 민사본안사건(가단(001), 가합(002), 가소(003), 나(004), 다(005)) 관련 표시방법변경
    var csDvsCd = dma_csBasCtt.get("csNo").substring(4,7);
    var csRcptYmd = (scutilo.isEmpty(dma_csBasCtt.get("csRcptYmd")))? "0" : dma_csBasCtt.get("csRcptYmd");
    var intCsRcptYmd = parseInt(csRcptYmd);
    if((csDvsCd === "001" || csDvsCd === "002" || csDvsCd === "003" || csDvsCd === "004" ||csDvsCd === "005") 
        && intCsRcptYmd >= ssgo.getProperties("clt.ssgo.stmp.rcpt.ymd")) {
        if(scutilo.isEmpty(dma_csBasCtt.get("stmpAtchAmt"))) {
            dma_csBasCtt.set("stmpAtchAmt", "0");
        }
        tr_stmpAmt.show("");    
    } else {
        tr_stmpAmt.hide();
    }
    
    // 송달료,보관금 view
    if(dma_csBasCtt.get("amtYn") === "Y") {
        txt_dlvrBlnc.hide("");
    } else {
        btn_chkDlvrBlnc.hide("");
    }
}
