/**
 * 변제현황조회 팝업을 호출한다.
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.btn_lqdtCurstInq_onclick = function(e) {
    var rpingaGyuljYn = dma_csBasCtt.get("rpingaGyuljYn"); //변제계획인가결정여부
    var ingaChk = dma_csBasCtt.get("ingaChk");   //최초변제수행여부
    
    // 인가결정 사건여부  Y : 인가결정 사건, N : 인가결정이 나지 않은 사건
    // N : 최초변제가 수행되어 출금액이 있는 경우 , Y : 최초변제가 수행되지 않는 경우
    if(rpingaGyuljYn === "Y" && ingaChk === "N") {
        requires("uiplugin.popup");
        
        var paramData = scwin.param;
        paramData.cortNm   = dma_csBasCtt.get("cortNm");    //법원명
        paramData.userCsNo = dma_csBasCtt.get("userCsNo");  //식별사건번호
        paramData.nrlnmDvsCd = dma_csBasCtt.get("nrlnmDvsCd");  //식별사건번호
        paramData.csNo = dma_csBasCtt.get("csNo");  //사건번호

        paramData.cortCd = dma_csBasCtt.get("cortCd");
        paramData.csDvsCd = dma_csBasCtt.get("csNo").substring(4,7);
        paramData.csSerial = dma_csBasCtt.get("csNo").substring(7);
        paramData.csYear = dma_csBasCtt.get("csNo").substring(0,4);
        
        // 변제현황 팝업
        window.open("" ,"lqdtCurstInqCtt", "toolbar=no, width=1100, height=800, directories=no, status=no, scrollorbars=no, resizable=no"); 
        ssgo.sendPostForm(ssgo.getCtxtPathUrl("/lqdtCurstInq.on"), paramData, "lqdtCurstInqCtt"); 

    } else {
        scutilo.showMsg(scglblo.MSG.INFO, scutilo.getMsg(ssgomsg.SSGO.I_0011), function () {
        });
    }

};

