/**
 * 화면제어 및 가공되는 데이터를 처리한다.
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.ufn_setDataArilPrcs = function() {
    var csDvsCd = dma_csBasCtt.get("csNo").substring(4,7);

    // 영상 여부
    if(dma_csBasCtt.get("vdeoJdcpcProgYn") != "Y") {
        txt_isVdeo.hide();
    }
    // 전자 여부
    if(dma_csBasCtt.get("lwstDvsCd") !== "2") {
        txt_isElctn.hide();
    }
    
    //개회사건(253)
    if('253'.indexOf(csDvsCd) > -1)  {
        scwin.ufn_trToggle("S", "tr_dvs1_1,tr_dvs1_2,tr_dvs1_3,tr_dvs1_4,tr_dvs1_5");
        scwin.ufn_trToggle("H", "tr_dvs2_1,tr_dvs2_2,tr_dvs2_3,tr_dvs3_1,tr_dvs3_2,tr_dvs3_3");

        // 채무자
        txt_dpwNm1.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("dpwNm"), dma_csBasCtt.get("wCnt")));

        // 재판부 fulltext구성
        var txtjdbnNm = dma_csBasCtt.get("jdbnNm");
        if(dma_csBasCtt.get("csPrsrvYn") !== "Y" && !scutilo.isEmpty(dma_csBasCtt.get("rhblCmsnrTelno"))) {
            txtjdbnNm += " (전화:" + dma_csBasCtt.get("rhblCmsnrTelno") + ")";
        }
        txt_jdbnNm1.setValue(txtjdbnNm);
        txt_jdbnNm.setValue(txtjdbnNm);

        // 종국내용
        var csUltmtYmdFmt = (!scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("csUltmtYmd")), 'yyyy.MM.dd') : "";
        txt_csUltmtDvsNm1.setValue(csUltmtYmdFmt + " " + dma_csBasCtt.get("csUltmtDvsNm"));

        // 게시결정일, 변제계획인가일
        let today = scdateo.getDateStr(new Date());
        if(!scutilo.isEmpty(dma_csBasCtt.get("csCmdcYmd")) && scdateo.getDaysBetween(dma_csBasCtt.get("csCmdcYmd"), today) < 0) { //오늘날짜보다 크면 안보여줌.
            dma_csBasCtt.set("csCmdcYmd", "");
        }
        if(!scutilo.isEmpty(dma_csBasCtt.get("repayKjDay")) && scdateo.getDaysBetween(dma_csBasCtt.get("repayKjDay"), today) < 0) { //오늘날짜보다 크면 안보여줌.
            dma_csBasCtt.set("repayKjDay", "");
        }

    //개확(254), 개보(255), 개기(290)
    } else if('254,255,290'.indexOf(csDvsCd) > -1)  {
        scwin.ufn_trToggle("S", "tr_dvs2_1,tr_dvs2_2,tr_dvs2_3");
        scwin.ufn_trToggle("H", "tr_dvs1_1,tr_dvs1_2,tr_dvs1_3,tr_dvs1_4,tr_dvs1_5,tr_dvs3_1,tr_dvs3_2,tr_dvs3_3");

        // 신청인, 상대방
        txt_dpwNm2.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("dpwNm"), dma_csBasCtt.get("wCnt")));
        txt_dppNm2.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("dppNm"), dma_csBasCtt.get("pCnt")));

        // 재판부 fulltext구성
        var txtjdbnNm = dma_csBasCtt.get("jdbnNm");
        if(dma_csBasCtt.get("csPrsrvYn") !== "Y" && !scutilo.isEmpty(dma_csBasCtt.get("rhblCmsnrTelno"))) {
            txtjdbnNm += " (전화:" + dma_csBasCtt.get("rhblCmsnrTelno") + ")";
        }
        txt_jdbnNm2.setValue(txtjdbnNm);
        txt_jdbnNm.setValue(txtjdbnNm);

        // 종국내용
        var csUltmtYmdFmt2 = (!scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("csUltmtYmd")), 'yyyy.MM.dd') : "";
        txt_csUltmtDvsNm2.setValue(csUltmtYmdFmt2 + " " + dma_csBasCtt.get("csUltmtDvsNm"));

    //그 외
    } else {
        scwin.ufn_trToggle("S", "tr_dvs3_1,tr_dvs3_2,tr_dvs3_3");
        scwin.ufn_trToggle("H", "tr_dvs1_1,tr_dvs1_2,tr_dvs1_3,tr_dvs1_4,tr_dvs1_5,tr_dvs2_1,tr_dvs2_2,tr_dvs2_3,");

        // 신청인, 상대방    
        txt_rprsAplcntNm.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("rprsAplcntNm"), dma_csBasCtt.get("ptnrCnt")));
        txt_rprsRspndnNm.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("rprsRspndnNm"), dma_csBasCtt.get("rqstrCnt")));

        // 재판부 fulltext구성
        var txtjdbnNm = dma_csBasCtt.get("jdbnNm");
        if("000100" !== dma_csBasCtt.get("cortCd") && !scutilo.isEmpty(dma_csBasCtt.get("cfupMarkNm"))) {
            txtjdbnNm += "(" + dma_csBasCtt.get("cfupMarkNm") + ")";
        }
        if(dma_csBasCtt.get("csPrsrvYn") !== "Y" && !scutilo.isEmpty(dma_csBasCtt.get("rhblCmsnrTelno"))) {
            txtjdbnNm += " (전화:" + dma_csBasCtt.get("rhblCmsnrTelno") + ")";
        }
        txt_jdbnNm3.setValue(txtjdbnNm);      
        txt_jdbnNm.setValue(txtjdbnNm);
        
        // 종국내용은 현재날짜보다 작을 경우에만 보여줌
        let today = scdateo.getDateStr(new Date());
        if(!scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd")) && scdateo.getDaysBetween(dma_csBasCtt.get("csUltmtYmd"), today) >= 0) {
            var csUltmtYmdFmt3 = (!scutilo.isEmpty(dma_csBasCtt.get("csUltmtYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("csUltmtYmd")), 'yyyy.MM.dd') : "";            
            txt_csUltmtDvsNm3.setValue(csUltmtYmdFmt3 + " " + dma_csBasCtt.get("csUltmtDvsNm"));
        } else  {
            txt_csUltmtDvsNm3.setValue("");
        }          
    }
    
    // 보존,폐기 view
    if(!scutilo.isEmpty(dma_csBasCtt.get("prsvCtt"))) {
        if(dma_csBasCtt.get("prsvCtt") === "보존") {
            txt_titPrsvCtt.setValue("보존여부");
            txt_prsvCtt.setValue("기록보존됨");
        } else if(dma_csBasCtt.get("prsvCtt") === "폐기") {
            txt_titPrsvCtt.setValue("폐기여부");
            txt_prsvCtt.setValue("기록폐기됨");
        }
        tr_prsvCtt.show("");
    } else {
        tr_prsvCtt.hide();
    }
    
    // 송달료,보관금 view
    if(dma_csBasCtt.get("amtYn") === "Y") {
        txt_dlvrBlnc.hide("");
        tr_lqdtCurstInq.hide();
    } else {
        //변제현황 조회 tr제어
        if('253'.indexOf(csDvsCd) > -1)  {
            tr_lqdtCurstInq.show("");
        } else {
            tr_lqdtCurstInq.hide();
        }
        
        btn_chkDlvrBlnc.hide("");
    }
}

/**
 * 테이블(tr) 태그의 토글처리를 한다.
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.ufn_trToggle = function(toggleDvs, tagIds) {
    if(scutilo.isEmpty(toggleDvs) || scutilo.isEmpty(tagIds)) {
        return false;
    }

    var tagObj = tagIds.split(",");
    for (let i=0; i< tagObj.length ; i++ ) {
        if (typeof $p.getComponentById(tagObj[i]) != "undefined" ) {
            if(toggleDvs === "S") {
                $p.getComponentById(tagObj[i]).show("");
            } else if(toggleDvs === "H") {
                $p.getComponentById(tagObj[i]).hide();
            }
        }
    }

}
