/**
 * submit 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbm_search_submit = function(e) {
}; 

/**
 * submitdone 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbm_search_submitdone = function(e) {
    var csRsltData = e.responseJSON.data;

    // 상단 당사자 콤보박스 정보 셋팅
    //dlt_acsCttLst.removeAll();
    //dlt_acsCttLst.setJSON(csRsltData.dlt_acsCttLst);

    // 새로 열릴 화면에 데이터를 전달 (dataObject를 통해 전달 가능)         
    var wfObj = {"dataObject": {"type" : "json", "name" : "paramData", "data" : null,}}; 

    // 가공처리되는 정보 ------------------------------------------------
    scwin.ufn_setDataArilPrcs();

    // 심급내용
    if(!ssgo.isLenZero(csRsltData.dlt_inscrtDtsLst)) {
        wfObj.dataObject.data = csRsltData.dlt_inscrtDtsLst;
        wfInscrtDtsLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F11.xml"), wfObj);  
        wfInscrtDtsLst.show("");  
    } else {
        wfInscrtDtsLst.hide();
    }

    if(dma_csBasCtt.get("isHrngProgCurst") === "Y" ) {
        // 심리진행현황
        if(!ssgo.isLenZero(csRsltData.dlt_hrngProgCurst)) {
            wfObj.dataObject.data = csRsltData.dlt_hrngProgCurst;
            wfHrngProgCurst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F20.xml"), wfObj);    
        }
    } else {
        // 최근기일내용
        wfObj.dataObject.data = csRsltData.dlt_rcntDxdyLst;
        wfRcntDxdyLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F32.xml"), wfObj);  
    }

    // 최근 제출서류 접수내용
    wfObj.dataObject.data = csRsltData.dlt_rcntSbmsnDocmtLst;
    wfRcntSbmsnDocmtLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F40.xml"), wfObj);    

    // 관련사건내용
    if(!ssgo.isLenZero(csRsltData.dlt_reltCsLst)) {
        wfObj.dataObject.data = csRsltData.dlt_reltCsLst;
        wfReltCsLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F50.xml"), wfObj);    
        wfReltCsLst.show("");  
    } else {
        wfReltCsLst.hide();
    }

    // 병합분리내역
    if(!ssgo.isLenZero(csRsltData.dlt_mergeCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_mergeCttLst;
        wfMergeCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003FE0.xml"), wfObj);    
        wfMergeCttLst.show("");
    } else {
        wfMergeCttLst.hide();
    }

    // 피고인및죄명내역
    if(!ssgo.isLenZero(csRsltData.dlt_acsCttLst)) {
        wfObj.dataObject.data = csRsltData;
        wfAcsCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F6B.xml"), wfObj);    
        wfAcsCttLst.show("");  
    } else {
        wfAcsCttLst.hide();
    }

    // 변호인내역
    if(!ssgo.isLenZero(csRsltData.dlt_atrnyCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_atrnyCttLst;
        wfAtrnyCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F71.xml"), wfObj);    
        wfAtrnyCttLst.show("");  
    } else {
        wfAtrnyCttLst.hide();
    }

    // 사건검색 후  callback
    ssgo.callbackCsSrchDetail(scwin.param, e);

};    

scwin.sbm_search_submiterror = function(e) {
    // 사건검색 후  callback    
    ssgo.callbackCsSrchDetail(scwin.param, e);
};

/**
 *********************************************************************************************************
 * Event Handler
 *********************************************************************************************************
 */
/**
 * 당사자 선택 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbx_btprtLst_onchange = function(info) {

    var param = scwin.param;
    param.btprtSeq = sbx_btprtLst.getValue();
    param.btprtNm  = dlt_acsCttLst.getCellData(sbx_btprtLst.getSelectedIndex(), "btprtNmOrg");

    // 인터셉터 우회처리
    param.captchaAnswer = "";
    //param.callDomain = "";  
    param.progCttDvs = "";
    param.csNo = dma_csBasCtt.get("csNo");
    param.encCsNo = dma_csBasCtt.get("encCsNo");

    $p.parent().scwin.ufn_reloadTab(1, param, "N"); 

    // 초기화
    dma_csBasCtt.reset();

    ssgo.selectCsSrch(param);
};

/**
 *********************************************************************************************************
 * Validate 
 *********************************************************************************************************
 */

/**
 *********************************************************************************************************
 * User Function
 *********************************************************************************************************
 */

/**
 * 화면제어 및 가공되는 데이터를 처리한다.
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.ufn_setDataArilPrcs = function() {

    // 피고인,피의자 명칭 셋팅
    txt_acsDvsNm.setValue(dma_csBasCtt.get("acsdDvsNm"));
    lbl_acsDvsNm.setValue(dma_csBasCtt.get("acsdDvsNm") + "명");

    sbx_btprtLst.setEventPause(null, true);   // event lock
    // 상단 당사자 콤보박스 default 셋팅
    for(let i = 0; i < dlt_acsCttLst.getRowCount(); i++) {
       if(dma_csBasCtt.get("btprtSeq") === dlt_acsCttLst.getCellData(i, dlt_acsCttLst.cellIdList[0])) {
           sbx_btprtLst.setSelectedIndex(i);
           break;
       }
    }
    sbx_btprtLst.setEventPause(null, false);  // event unlock

    // 형사본안류는 대표죄명안보이게 처리
    // 초:091,초기:206,초보:205,초적:204,초재:422,로:083,모:085,보:087,감초:092,감모:086,감로:084
    var csDvsCd = dma_csBasCtt.get("csDvsCd");
    if(csDvsCd == "091" || csDvsCd == "206" || csDvsCd == "205" || csDvsCd == "204"
    || csDvsCd == "422" || csDvsCd == "083" || csDvsCd == "085" || csDvsCd == "087"
    || csDvsCd == "092" || csDvsCd == "086" || csDvsCd == "084") {
        lbl_csNm.show("");
        txt_csNm.show("");
        txt_isElctn2.hide();        
    } else {
        lbl_csNm.hide();
        txt_csNm.hide();
        // [20240722, 형사전자소송]        
        txt_isElctn.hide();
    }

    // [20240722, 형사전자소송]
    // 소송구분코드 조회 추가.
    // 사건명 좌측에 "[전자]" 표시를 기본으로, 소송구분코드 <> '2'(전자소송)이면 미표시.

    if(dma_csBasCtt.get("lwstDvsCd") != "2") {
        txt_isElctn.hide();
        txt_isElctn2.hide();
    } 

    // 재판부 fulltext구성
    var txtjdbnNm = dma_csBasCtt.get("jdbnNm");
    if("000100" !== dma_csBasCtt.get("cortCd") && !scutilo.isEmpty(dma_csBasCtt.get("cfupMarkNm"))) {
        txtjdbnNm += "(" + dma_csBasCtt.get("cfupMarkNm") + ")";
    }
    if(dma_csBasCtt.get("csPrsrvYn") !== "Y" && !scutilo.isEmpty(dma_csBasCtt.get("jdbnTphnGdncCtt"))) {
        txtjdbnNm += " (전화:" + dma_csBasCtt.get("jdbnTphnGdncCtt") + ")";
    }
    txt_jdbnNm.setValue(txtjdbnNm);

    // 종국내용은 현재날짜보다 작을 경우에만 보여줌
    let today = scdateo.getDateStr(new Date());
    if(!scutilo.isEmpty(dma_csBasCtt.get("btprtUltmtYmd")) && scdateo.getDaysBetween(dma_csBasCtt.get("btprtUltmtYmd"), today) >= 0) {
        txt_ultmtDvsNm.setValue(dma_csBasCtt.get("crmcsUltmtDvsNm"));
    } else  {
        txt_ultmtDvsNm.setValue("");
    }

    // 상소제기내용 setting 
    if("000100" !== dma_csBasCtt.get("cortCd")) {   // 대법원이 아닐경우에만 보여줌
        var aplPrpndCtt = "";   //상소제기내용
        var code   = "";
        var value  = "";
        var inscrt = "1";   //심급 참고) 1심=230, 231, 077, 113, 075, 112, 076, 117 / 3심=082, 081, 116, 119
        var csDvsCd = dma_csBasCtt.get("csDvsCd");

        if( csDvsCd === "079" || csDvsCd === "115" || csDvsCd === "080" || csDvsCd === "118" ) {
            inscrt = "2"; //2심        
        }

        if( inscrt === "1" && !scutilo.isEmpty(dma_csBasCtt.get("apelCrmcsLwstRltnrDvsCd")) ) { //항소
            if( !scutilo.isEmpty(dma_csBasCtt.get("acsApelPrpndYmd")) ) {
                var acsApelPrpndYmdFmt = (!scutilo.isEmpty(dma_csBasCtt.get("acsApelPrpndYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("acsApelPrpndYmd")), 'yyyy.MM.dd') : "";
               aplPrpndCtt = acsApelPrpndYmdFmt + " ";
            }
            code = dma_csBasCtt.get("apelCrmcsLwstRltnrDvsCd");

        } else if( inscrt === "2" && !scutilo.isEmpty(dma_csBasCtt.get("applCrmcsLwstRltnrDvsCd")) ) { //상고

            if( !scutilo.isEmpty(dma_csBasCtt.get("acsApplPrpndYmd")) ) {
                var acsApplPrpndYmdFmt = (!scutilo.isEmpty(dma_csBasCtt.get("acsApplPrpndYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("acsApplPrpndYmd")), 'yyyy.MM.dd') : "";
                aplPrpndCtt = acsApplPrpndYmdFmt + " ";
            }
            code = dma_csBasCtt.get("applCrmcsLwstRltnrDvsCd");
        }

        if( !scutilo.isEmpty(code) ) {
            if( code === "01" ) value = "검사상소";
            else if( code === "02" ) value = "피고인상소";
            else if( code === "03" ) value = "변호인상소";
            else if( code === "04" ) value = "대리인상소";
            else if( code === "05" ) value = "보조인상소";
            else if( code === "06" ) value = "증인상소";
            else if( code === "07" ) value = "감정인상소";
            else if( code === "08" ) value = "통역인상소";
            else if( code === "09" ) value = "번역인상소";
            else if( code === "10" ) value = "청구인상소";
            else if( code === "11" ) value = "피의자상소";
            else if( code === "12" ) value = "배상신청인상소";
            else if( code === "13" ) value = "직권상소";
            else if( code === "14" ) value = "쌍방상소";
            else if( code === "99" ) value = "기타상소";

            aplPrpndCtt += value;
        }    

        if( !scutilo.isEmpty(dma_csBasCtt.get("aplPrpndRsltYmd")) && !scutilo.isEmpty(dma_csBasCtt.get("aplPrpndRsltCd")) ) {
            var aplPrpndRsltYmdFmt = (!scutilo.isEmpty(dma_csBasCtt.get("aplPrpndRsltYmd"))) ? $p.getFormattedDate(scdateo.getDateObj(dma_csBasCtt.get("aplPrpndRsltYmd")), 'yyyy.MM.dd') : "";
            aplPrpndCtt += " / " + aplPrpndRsltYmdFmt + " " + dma_csBasCtt.get("aplPrpndRsltNm");
        }

        txt_aplPrpndCtt.setValue(aplPrpndCtt);
        tr_aplPrpndCtt.show("");
    } else {
        tr_aplPrpndCtt.hide();
    }

    // 보존,폐기 view
    if(!scutilo.isEmpty(dma_csBasCtt.get("prsvCtt"))) {
        if(dma_csBasCtt.get("prsvCtt") === "보존") {
            txt_titPrsvCtt.setValue("보존여부");
            txt_prsvCtt.setValue("기록보존됨");
        } else if(dma_csBasCtt.get("prsvCtt") === "폐기") {
            txt_titPrsvCtt.setValue("폐기여부");
            txt_prsvCtt.setValue("기록폐기됨");
        }
        tr_prsvCtt.show("");
    } else {
        tr_prsvCtt.hide();
    }


}
