 */
scwin.param   = {};  // 파라메터


/**
 * 화면 pageload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.onpageload = function() {
    scwin.param = com.data.getParameter().paramInfo;

    ssgo.initCsSrch();
};

/**
 * 화면 pageunload 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.onpageunload = function() {
};

/**
 *********************************************************************************************************
 * Submission Event Handler
 *********************************************************************************************************
 */
/**
 * submit 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbm_search_submit = function(e) {
}; 
 
/**
 * submitdone 이벤트
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.sbm_search_submitdone = function(e) {
    var csRsltData = e.responseJSON.data;
    
    // 새로 열릴 화면에 데이터를 전달 (dataObject를 통해 전달 가능)         
    var wfObj = {"dataObject": {"type" : "json", "name" : "paramData", "data" : null,}}; 
    
    // 가공처리되는 정보 ------------------------------------------------
    scwin.ufn_setDataArilPrcs();
    
    // 관련사건내용
    if(!ssgo.isLenZero(csRsltData.dlt_reltCsLst)) {
        wfObj.dataObject.data = csRsltData.dlt_reltCsLst;
        wfReltCsLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F50.xml"), wfObj);    
    } else {
        wfReltCsLst.hide();
    }
    
    // 피고인내역
    if(!ssgo.isLenZero(csRsltData.dlt_acsCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_acsCttLst;
        wfAcsCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F6A.xml"), wfObj);    
    } else {
        wfAcsCttLst.hide();
    }

    // 변호인내역
    if(!ssgo.isLenZero(csRsltData.dlt_atrnyCttLst)) {
        wfObj.dataObject.data = csRsltData.dlt_atrnyCttLst;
        wfAtrnyCttLst.setSrc(ssgo.getCtxtPathUrl("/ui/ssgo003/SSGO003F71.xml"), wfObj);    
    } else {
        wfAtrnyCttLst.hide();
    }
    
    // 사건검색 후  callback
    dma_csBasCtt.set("csNm", dma_csBasCtt.get("csCrimeNm"));
    ssgo.callbackCsSrchDetail(scwin.param, e);
    
};    

scwin.sbm_search_submiterror = function(e) {
    // 사건검색 후  callback    
    ssgo.callbackCsSrchDetail(scwin.param, e);
};


/**
 *********************************************************************************************************
 * Event Handler
 *********************************************************************************************************
 */


/**
 *********************************************************************************************************
 * Validate 
 *********************************************************************************************************
 */

/**
 *********************************************************************************************************
 * User Function
 *********************************************************************************************************
 */

/**
 * 화면제어 및 가공되는 데이터를 처리한다.
 *
 * ============ ============ ==========================
 *  Date         Author       Description
 * ============ ============ ==========================
 * 2022.05.10.   최연구       [NELS]최초생성
 * ============ ============ ==========================
 */
scwin.ufn_setDataArilPrcs = function() {
    
    // 피고인 명칭 셋팅
    txt_rprsRqstrNm.setValue(ssgo.getCsBtprNmInclCnt(dma_csBasCtt.get("rprsAcsNm"), dma_csBasCtt.get("acsdCnt")));

    // 재판부 fulltext구성
    var txtjdbnNm = dma_csBasCtt.get("jdbnNm");
    if(!scutilo.isEmpty(dma_csBasCtt.get("jdbnTelno"))) {
        txtjdbnNm += " (전화:" + dma_csBasCtt.get("jdbnTelno") + ")";
    }
    txt_jdbnNm.setValue(txtjdbnNm);

    //약식명령일
    if(!scutilo.isEmpty(dma_csBasCtt.get("smryOrdYmd"))) {
        tr_smryOrdYmd.show("");
    } else {
        tr_smryOrdYmd.hide();
    }
    
    // 보존,폐기 view
    if(!scutilo.isEmpty(dma_csBasCtt.get("prsvCtt"))) {
        if(dma_csBasCtt.get("prsvCtt") === "보존") {
            txt_titPrsvCtt.setValue("보존여부");
            txt_prsvCtt.setValue("기록보존됨");
        } else if(dma_csBasCtt.get("prsvCtt") === "폐기") {
            txt_titPrsvCtt.setValue("폐기여부");
            txt_prsvCtt.setValue("기록폐기됨");
        }
        tr_prsvCtt.show("");
    } else  {
        tr_prsvCtt.hide();
    }

    // [20240801, 형사전자소송]
    // 소송구분코드 조회 추가.
    // 사건번호 우측에 "[전자]" 표시를 기본으로, 소송구분코드 <> '2'(전자소송)이면 미표시.

    if (dma_csBasCtt.get("lwstDvsCd") != "2") {
        txt_isElctn.hide();
    }    

}
